import React, { useRef } from "react";

import styles from './UseForm.module.css';


const UserForm = (props) => {

  const enteredName = useRef('');
  const enteredNumber = useRef('');
  const enteredMonth = useRef('');
  const enteredYear = useRef('');



  const submitHandler = (event) => {
    event.preventDefault();

    const name = enteredName.current.value;
    const number = enteredNumber.current.value;
    const month = enteredMonth.current.value;
    const year = enteredYear.current.value;

    const cardData = {
      name: name,
      number: number,
      month: month,
      year: year
    };

    props.onCardInfo(cardData);


    enteredName.current.value = '';
    enteredNumber.current.value;
    enteredMonth.current.value;
    enteredYear.current.value;

  }


  return <section className={styles['form-section']}>

    <form onSubmit={submitHandler}>

      <div className={styles['input-container']}>

        <div className={styles['input-container__input']}>
          <label>CARDHOLDER NAME</label>
          <br></br>
          <input 
          type='text' 
          placeholder="e.g. Jane Appleseed"
          ref={enteredName}
          ></input>
        </div>

        <div className={styles['input-container__input']}>
          <label>CARD NUMBER</label>
          <br></br>
          <input 
          type='number' 
          placeholder="e.g. 1234 5678 9123 0000"
          ref={enteredNumber}
          ></input>
        </div>

        <div className={styles['input-container-special']}>

          <div className={styles['input-container__input']}>
            <label>EXP. DATE (MM/YY)</label>
            <br></br>
            <input 
            type='number' 
            min='1' max='12' 
            placeholder="MM"
            ref={enteredMonth}
            ></input>

            <input 
            type='number' 
            min='23' 
            max='30' 
            placeholder="YY"
            ref={enteredYear}
            ></input>
          </div>

          <div className={styles['input-container__input']}>
            <label>CVC</label>
            <br></br>
            <input 
            type='number' 
            placeholder="e.g. 123"
            ></input>
          </div>
        </div>


      </div>

      <button type="submit">Confirm</button>

    </form>
  </section>
}

export default UserForm;
