import React, { useContext, useState } from "react";
import styles from '../Components/Main.module.css';
import Section from "./UI/Section";
import UserForm from "./UserForm/UserForm";
import cardDetailsContext from "../context/card-context";
import Modal from "./UI/Modal/Modal";
import ErrorModal from "./UI/Modal/ErrorModal";


const Main = () => {

  const cardDataDefault = {
    name: 'Jane Applewood',
    number: '0000 0000 0000 0000',
    month: '00',
    year: '00',
    cvv: '000'
  };

  const cardCtx = useContext(cardDetailsContext);

  const successSubmit = cardCtx.didSubmitData;

  console.log(cardCtx.error);
  console.log(successSubmit)

  const [cardData, setCardData] = useState(cardDataDefault);

  const cardInfoHandler = (data) => {
    setCardData(data)
  }


return (
    <main className={styles.main}>
      <Section cardInfo={cardData}/>
      {!successSubmit && <UserForm onCardInfo={cardInfoHandler}/>}
      {}
      {successSubmit && cardCtx.error && <ErrorModal></ErrorModal>}
      {successSubmit && !cardCtx.error && <Modal></Modal>}
    </main>
)
}

export default Main;
