const cardDetailsContext = React.createContext({
  name: '',
  cardNumber: '',
  'Expiration date': {
    month: '',
    year: '',
  },
  cvv: '',
  error:  null,
  isDataSent: null,
  didSubmitData:null,
  submitCardDetails: () => {},
  continueModal: () => {},
})

export default cardDetailsContext;
