import React, { useRef } from "react";

import styles from './UseForm.module.css';


const UserForm = () => {

  const cardNameRef = useRef();
  const cardNumberRef = useRef();
  const cardMonthRef = useRef();
  const cardYearRef = useRef();
  const cardCVVRef = useRef();

  const submitHandler = (event) => {
    event.preventDefault();

    const enteredCardName = cardNameRef.current.value;
    const enteredCardNumber = cardNumberRef.current.value;
    const enteredCardMonth = cardMonthRef.current.value;
    const enteredCardYear = cardYearRef.current.value;
    const enteredCardCVV = cardCVVRef.current.value;

    const formArray = {
      cardName: enteredCardName,
      cardNumber: enteredCardNumber,
      cardMonth: enteredCardMonth,
      cardYear: enteredCardYear,
      cardCVV: enteredCardCVV
    }

    console.log(formArray);


    cardNameRef.current.value;
    cardNumberRef.current.value;
    cardMonthRef.current.value;
    cardYearRef.current.val;
    cardCVVRef.current.value = ' ';
  }


  return <section className={styles['form-section']}>

    <form onSubmit={submitHandler}>

      <div className={styles['input-container']}>

        <div className={styles['input-container__input']}>
          <label>CARDHOLDER NAME</label>
          <br></br>
          <input 
          type='text' 
          placeholder="e.g. Jane Appleseed"
          ref={cardNameRef}
          ></input>
        </div>

        <div className={styles['input-container__input']}>
          <label>CARD NUMBER</label>
          <br></br>
          <input 
          type='number' 
          placeholder="e.g. 1234 5678 9123 0000"
          ref={cardNumberRef}
          ></input>
        </div>

        <div className={styles['input-container-special']}>

          <div className={styles['input-container__input']}>
            <label>EXP. DATE (MM/YY)</label>
            <br></br>
            <input 
            type='number' 
            min='1' max='12' 
            placeholder="MM"
            ref={cardMonthRef}
            ></input>
            <input 
            type='number' 
            min='23' 
            max='30' 
            placeholder="YY"
            ref={cardYearRef}
            ></input>
          </div>

          <div className={styles['input-container__input']}>
            <label>CVC</label>
            <br></br>
            <input 
            type='number' 
            placeholder="e.g. 123"
            ref={cardCVVRef}
            ></input>
          </div>
        </div>


      </div>

      <button type="submit">Confirm</button>

    </form>
  </section>
}

export default UserForm;
