import { useState } from "react";
import cardDetailsContext from "./card-context";

const ContextProvider = (props) => {

  const [submitData, setSubmitData] = useState(false);

  const submitCardDetailsHandler = (formData) => {
    setSubmitData(true);
    fetch('https://react-section14-htpp-default-rtdb.firebaseio.com/card-details.json', {
      method: 'POST',
      body: JSON.stringify(formData),
    })


  }


  const cardContext = {
    name: '',
    cardNumber: '',
    'Expiration date': {
      month: '',
      year: '',
    },
    cvv: '',
    submitCardDetails: submitCardDetailsHandler,
  }


  return <cardDetailsContext.Provider value={cardContext}>
    {props.children}
  </cardDetailsContext.Provider>
}

export default ContextProvider;
