import React, {useState } from "react";
import useInput from "../../hooks/use-input";

import styles from './UseForm.module.css';



const UserForm = (props) => {

  const [nameInput, setNameInput] = useState('');
  const [isNameOnBlur, setIsNameOnBlur] = useState(false);

  const {
    inputValue ,
    isInputValue: isInputInvalid,
    inputChangeHandler,
    inputBlurHandler,
  } = useInput()


  const nameInputHandler = (event) => {
    setNameInput(event.target.value);
  }

  const nameBlurHandler = () => {
    setIsNameOnBlur(true);
  }

  const isNameValid = nameInput.trim() !== '';

  console.log(isNameValid);
  const isNameInputInvalid = !isNameValid && isNameOnBlur;

  console.log(isNameInputInvalid);


  let isFormValid;

  const submitHandler = (event) => {
    event.preventDefault();

    isFormValid = isNameInputInvalid;

    if (!isFormValid) {
      return
    }

    console.log("Form is Valid");

    setNameInput('');
    setIsNameOnBlur(false);

  }

  return <section className={styles['form-section']}>

    <form onSubmit={submitHandler}>

      <div className={styles['input-container']}>

        <div className={styles['input-container__input']}>
          <label>CARDHOLDER NAME</label>
          <br></br>
          <input 
          type='text' 
          placeholder="e.g. Jane Appleseed"
          onChange={nameInputHandler}
          onBlur={nameBlurHandler}
          ></input>
          {isNameInputInvalid && <p className={styles.warning}>Can't be blank</p> }
        </div>

        <div className={styles['input-container__input']}>
          <label>CARD NUMBER</label>
          <br></br>
          <input 
          type='number' 
          placeholder="e.g. 1234 5678 9123 0000"
          ></input>
        </div>

        <div className={styles['input-container-special']}>

          <div className={styles['input-container__input']}>
            <label>EXP. DATE (MM/YY)</label>
            <br></br>
            <input 
            type='number' 
            min='1' max='12' 
            placeholder="MM"
            ></input>
            <input 
            type='number' 
            min='23' 
            max='30' 
            placeholder="YY"
            ></input>
          </div>

          <div className={styles['input-container__input']}>
            <label>CVC</label>
            <br></br>
            <input 
            type='number' 
            placeholder="e.g. 123"
            ></input>
          </div>
        </div>


      </div>

      <button type="submit">Confirm</button>

    </form>
  </section>
}

export default UserForm;
