import React, { useState } from "react";

import styles from './UseForm.module.css';


const UserForm = (props) => {

  const [cardName, setCardName] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [cardMonth, setCardMonth] = useState('');
  const [cardYear, setCardYear] = useState('');
  const [cardCvv, setCardCvv] = useState('');


  const submitHandler = (event) => {
    event.preventDefault();

  }

  const cardNameHandler = (event) => {
    setCardName(event.target.value);
    props.onCardName(cardName);
  }

  const cardNumberHandler = (event) => {
    setCardNumber(event.target.value);
    props.onCardNumber(cardNumber);
  }

  const cardMonthHandler = (event) => {
    setCardMonth(event.target.value);
    props.onCardMonth(cardMonth);
  }

  const cardYearHandler = (event) => {
    setCardYear(event.target.value);
    props.onCardYear(cardYear);
  }

  const cardCvvHandler = (event) => {
    setCardCvv(event.target.value);
    props.onCarCvv(cardCvv);
  }


  return <section className={styles['form-section']}>

    <form onSubmit={submitHandler}>

      <div className={styles['input-container']}>

        <div className={styles['input-container__input']}>
          <label>CARDHOLDER NAME</label>
          <br></br>
          <input 
          type='text' 
          placeholder="e.g. Jane Appleseed"
          onChange={cardNameHandler}
          ></input>
        </div>

        <div className={styles['input-container__input']}>
          <label>CARD NUMBER</label>
          <br></br>
          <input 
          type='number' 
          placeholder="e.g. 1234 5678 9123 0000"
          ></input>
        </div>

        <div className={styles['input-container-special']}>

          <div className={styles['input-container__input']}>
            <label>EXP. DATE (MM/YY)</label>
            <br></br>
            <input 
            type='number' 
            min='1' max='12' 
            placeholder="MM"
            ></input>
            <input 
            type='number' 
            min='23' 
            max='30' 
            placeholder="YY"
            ></input>
          </div>

          <div className={styles['input-container__input']}>
            <label>CVC</label>
            <br></br>
            <input 
            type='number' 
            placeholder="e.g. 123"
            ></input>
          </div>
        </div>


      </div>

      <button type="submit">Confirm</button>

    </form>
  </section>
}

export default UserForm;
