import React, { useRef, useState } from "react";

import styles from './UseForm.module.css';

let name;
let number;
let month;
let year;
let cvv;
let counter = 0;

const UserForm = (props) => {

  const [error, setError] = useState(false)

  const enteredName = useRef('');
  const enteredNumber = useRef('');
  const enteredMonth = useRef('');
  const enteredYear = useRef('');
  const enteredCvv = useRef('');



  const submitHandler = (event) => {
    event.preventDefault();


    if (enteredName.current.value.trim().length > 0) {
      name = enteredName.current.value;
      counter++;
    }

    if(enteredNumber.current.value.trim().length === 16) {
      number = enteredNumber.current.value;
    }

    if (enteredMonth.current.value.trim().length > 0 || enteredYear.current.value.trim().length > 0) {
      if (enteredMonth.current.value.trim().length === 2 || enteredYear.current.value.trim().length === 2) {
        month = enteredMonth.current.value;
        year = enteredYear.current.value;
      }
    } else {

    }

    if  (enteredCvv.current.value.trim() === 3) {
      cvv = enteredCvv.current.value;
    }


    const cardData = {
      name: name,
      number: number,
      month: month,
      year: year,
      cvv: cvv,
    };


    if (name.trim().length > 0) {
      setError(false)
    } else {
      setError(true);
    }

    props.onCardInfo(cardData);


    enteredName.current.value = '';
    enteredNumber.current.value = '';
    enteredMonth.current.value = '';
    enteredYear.current.value = '';
    enteredYear.current.value ='';
  }


  return <section className={styles['form-section']}>

    <form onSubmit={submitHandler}>

      <div className={styles['input-container']}>

        <div className={styles['input-container__input']}>
          <label>CARDHOLDER NAME</label>
          <br></br>
          <input 
          type='text' 
          placeholder="e.g. Jane Appleseed"
          ref={enteredName}
          ></input>
          {error ? <p className={styles.warning}>Can't be blank</p> : ''}
        </div>

        <div className={styles['input-container__input']}>
          <label>CARD NUMBER</label>
          <br></br>
          <input 
          type='number' 
          placeholder="e.g. 1234 5678 9123 0000"
          ref={enteredNumber}
          ></input>
        </div>

        <div className={styles['input-container-special']}>

          <div className={styles['input-container__input']}>
            <label>EXP. DATE (MM/YY)</label>
            <br></br>
            <input 
            type='number' 
            min='1' max='12' 
            placeholder="MM"
            ref={enteredMonth}
            ></input>
            {error ? <p className={styles.warning}>Can't be blank</p> : ''}
            <input 
            type='number' 
            min='23' 
            max='30' 
            placeholder="YY"
            ref={enteredYear}
            ></input>
          </div>

          <div className={styles['input-container__input']}>
            <label>CVC</label>
            <br></br>
            <input 
            type='number' 
            placeholder="e.g. 123"
            ref={enteredCvv}
            ></input>
          </div>
        </div>


      </div>

      <button type="submit">Confirm</button>

    </form>
  </section>
}

export default UserForm;
